            "item_name_3": 2500
        },
        "Bidder 4": {}
    }
}

# Calculate total profits for each bidder
total_profits = {}
for bidder in bidding_properties["bidders"]:
    total_profit = 0
    for item, bid in bidding_properties["bidders"][bidder].items():
        total_profit += bid
    total_profits[bidder] = total_profit

# Determine winning bidders
winning_bids = {}
for bidder in bidding_properties["bidders"]:
    for item, bid in bidding_properties["bidders"][bidder].items():
        if bid > winning_bids.get(bidder, 0):
            winning_bids[bidder] = {item: bid}

# Output the current status
current_status = {
    "remaining_budget": bidding_properties["remaining_budget"],
    "total_profits": total_profits,
    "wining_bids": winning_bids
}

# Convert to JSON
json_output = json.dumps(current_status, indent=4)
print(json_output)
